mapper.select:
SELECT IF(COUNT(*) > 0, TRUE, FALSE)
FROM host
WHERE host_address = #{host_address}

SELECT img1, img2, img3, img4, img5, img6
FROM host_imgs
WHERE host_num = #{host_num}

SELECT host_available_day
FROM host_available_days
WHERE host_num = #{host_num}

SELECT item1, item2, item3, item4, item5, item6, item7, item8, item9, item10
FROM host_provide_items
WHERE host_num = #{host_num}

mapper.update:
UPDATE host
SET member_email = #{member_email}, host_address=#{host_address}, host_type=#{host_type}, host_intro=#{host_intro}
WHERE host_num = #{host_num}
