mapper.select:
SELECT img1, img2, img3, img4, img5, img6
FROM host_images
WHERE host_num = #{host_num}

mapper.update:
UPDATE host
SET
member_email = #{member_email}, host_address=#{host_address},
host_type=#{host_type}, host_intro=#{host_intro}
WHERE host_num =
#{host_num}

mapper.select:
SELECT COUNT(*)
FROM host_available_days
WHERE host_num = #{host_nume}
AND is_reservation = FALSE
AND host_available_day IN

	#{day}
